import React, { useState, useEffect } from "react";
import './main.css';
import Navbar from "./components/navbar";

export default function Main(){
    const [videoId, setvideoId] = useState("oa8kjoT-MrM");
    const [accessToken, setToken] = useState();
    const [album, setAlbum] = useState();

    const clientId = "b59b5bedc995467cb3441d3ed84082a0";
    const clientSecret = "7a24b348d2534bf98e50d5a4a8581aa4";

    function getAuthorization(){
        const redirectUri = 'http://localhost:3000';
        const scopes = 'user-read-playback-state user-read-currently-playing'; 
        const authUrl = `https://accounts.spotify.com/authorize?response_type=code&client_id=${clientId}&scope=${encodeURIComponent(scopes)}&redirect_uri=${encodeURIComponent(redirectUri)}`;

        window.open(authUrl, '_blank', 'width=800,height=600')
    };

    async function getToken(){
        try {
            const result = await fetch('https://accounts.spotify.com/api/token', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': 'Basic ' + btoa(clientId + ':' + clientSecret)
                },
                body: 'grant_type=client_credentials'
            });
            const data = await result.json();
            setToken(data.access_token);
            return;
        } catch (error){
            alert("Token could not be retrieved!")
        }
    }

    async function getAlbum(id){
        const result = await fetch(`https://api.spotify.com/v1/albums/${id}`, {
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${accessToken}`
            }   
        });
        const data = result.json();
        setAlbum(data.name)
    }

    // async function getCurSong(){
    //     const result = await fetch('https://api.spotify.com/v1/me/player/currently-playing', {
    //         method: 'GET',
    //         headers: {
    //             'Authorization': `Bearer ${accessToken}`
    //         }
    //     });
    //     if (result.status === 404){
    //         songName = "No songs is currently playing!"
    //         return null;
    //     }
    //     const data = await result.json();
    //     songName = data.name;
    //     document.getElementById("song").innerText = songName;
    // }

    return (
        <div className="bg">
            <Navbar />
            <div className="mvPlayer">
            <iframe
                className="player"
                src={`https://www.youtube.com/embed/${videoId}`}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                title="YouTube video player"
            ></iframe>
            </div>

            <button onClick={getAuthorization}>Authorize</button>
            <button onClick={getToken}>Get token</button>
            <h1 id="song">{album}</h1>
        </div>

    )
}
