import React from "react";
import './main.css';

export default function Authorize(){
    const api_url = 'http://localhost:3001'

    function userAuthorize(){
        window.location.href = `${api_url}/auth`;
    }

    return (
        <div className="authBg">
            <div className="authorization">
                <p>In order to fully utilize SpotMV, you will need to authorize your Spotify account by clicking the button below.</p>
                <button onClick={userAuthorize}>Authorize</button>
            </div>

        </div>

    )
}
