import React, { useState, useEffect } from "react";
import './main.css';
import Navbar from "./components/navbar";

export default function Main(){
    const searchParams = new URLSearchParams(window.location.search);

    const [videoId, setvideoId] = useState("oa8kjoT-MrM");
    const [song, setSong] = useState();
    const [next, setNext] = useState(false);
    const [isPlaying, setPlaying] = useState(false);
    const [access_token, setToken] = useState(searchParams.get('access_token'));

    useEffect(() => {
        const intervalId = setInterval(checkPlayState, 5000);
        if (isPlaying) {
            getCurSong();
            return () => clearInterval(intervalId);
        }
    }, [next]);

    async function getCurSong(){
        const result = await fetch('https://api.spotify.com/v1/me/player/currently-playing', {
            method: 'GET',
            headers: {
                'Authorization': `Bearer ${access_token}`
            }
        });
        try {
            const data = await result.json();
            setSong(data.item.name + " by " + data.item.artists[0]['name']);

            if (isPlaying){
                //set timer for behavior when song ends
                console.log("playing")
                setTimeout(() => {
                    setNext(!next);
                    //change the time to be based on the youtube mv length later
                }, data.item.duration_ms-data.progress_ms)
            } else {
                setSong("Play a song on your Spotify Player!")
            }
        } catch {
            setSong("Advertisement")
        }
    }

    const checkPlayState = async () => {
        const result = await fetch('https://api.spotify.com/v1/me/player', {
            headers: {
                'Authorization': `Bearer ${access_token}`
            }
        });
        const data = await result.json();
        try {
            setPlaying(data.is_playing);
        } catch {
            setPlaying(true);
        }
    }


    return (
        <div className="bg">
            <Navbar />
            <div className="mvPlayer">
            <iframe
                className="player"
                src={`https://www.youtube.com/embed/${videoId}`}
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                title="YouTube video player"
            ></iframe>
            </div>

            <h1>{song}</h1>
        </div>

    )
}
