import React, { useState, useEffect } from "react";
import './main.css';
import Navbar from "./components/navbar";
import Youtube from 'react-youtube'

export default function Main(){
    const searchParams = new URLSearchParams(window.location.search);

    //spotify vars
    const [song, setSong] = useState("Play a song on your Spotify Player!");
    const [user, setUser] = useState()
    const [isPlaying, setPlaying] = useState(false);
    const [nextState, setNext] = useState(false);
    const [accessToken, setToken] = useState(searchParams.get('access_token'));
    const [refreshToken, setRefresh] = useState(searchParams.get('refresh_token'))

    //youtube vars
    const [videoId, setvideoId] = useState("1XqIWr_WqM4");
    const API_KEY = "AIzaSyBuf83t9pfPd0GJPaFwqg-gkIP6dx195Zw";

    const getRefreshToken = async () => {
        try {
            const result = await fetch(`http://localhost:3001/refresh_token/${refreshToken}`);
            if (!result.ok){
                throw new Error(`Network response was not ok: ${result.statusText}`);
            }
            const token = JSON.parse(await result.text());
            setToken(token.access_token);
            setRefresh(token.refresh_token);
        } catch (error) {
            console.error(error);
        }
    }
    //get new access token every hour
    useEffect(() => {
        const intervalId = setInterval(getRefreshToken, 3600000);
        return () => clearInterval(intervalId);
    })

    const checkPlayState = async () => {
        try {
            const result = await fetch('https://api.spotify.com/v1/me/player', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${accessToken}`
                }
            });
            try {
                const data = JSON.parse(await result.text());
                setPlaying(data.is_playing);
                if (isPlaying){
                    try{
                        if (song != data.item.name + " by " + data.item.artists[0]['name']){
                            setNext(!nextState);
                        } else {
                            return;
                        }
                        setSong(data.item.name + " by " + data.item.artists[0]['name']);
                    } catch {
                        setSong("Advertisement Break")
                    }
                } else {
                    setSong("Play a song on your Spotify Player!");
                }
            } catch {
                setPlaying(false);
            }
        } catch (error){
            console.error(error);
            setPlaying(false);
        }
    }
    useEffect(() => {
        const intervalId = setInterval(checkPlayState, 4000);
        return () => clearInterval(intervalId);
    });

    async function getVideoID(){
        try {
            const result = await fetch(`https://www.googleapis.com/youtube/v3/search?key=${API_KEY}&q=${song} Song Official MV&type=video&part=snippet&maxResults=1`);
            const data = await result.json();
            setvideoId(data.items[0].id.videoId);
        } catch {
            alert("Limited to 100 songs a day!")
        }
    }
    useEffect(() => {
        getVideoID();
    }, [nextState])

    async function getUserName() {
        try {
            const result = await fetch('https://api.spotify.com/v1/me', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${accessToken}`
                }
            });
            const data = await result.json();
            setUser(data.display_name);
        } catch {
            setUser('ERROR');
        }
    }
    //gets user name everytime the component renders
    useEffect(() => {
        getUserName();
    })

    const opts = {
        height: "73vh",
        width: "95vw",
        playerVars: {
          autoplay: 1
        }
      };

    return (
        <div className="bg">
            <Navbar name={user}/>
            <div className="mvPlayer">
            {/* <iframe
                className="player"
                src={`https://www.youtube.com/embed/${videoId}?autoplay=1`}
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                title="YouTube video player"
            ></iframe> */}
                <Youtube videoId={videoId} opts={opts}/>

            </div>

            <h1>{song}</h1>
        </div>

    )
}
