import React, { useState, useEffect } from "react";
import './main.css';
import Navbar from "./components/navbar";

export default function Main(){
    const [videoId, setvideoId] = useState("oa8kjoT-MrM");
    const [token, setToken] = useState();
    let songName = "ming";

    const clientId = "b59b5bedc995467cb3441d3ed84082a0";
    const clientSecret = "7a24b348d2534bf98e50d5a4a8581aa4";

    useEffect(() => {
        getToken();
    });

    async function getToken(){
        const result = await fetch('https://accounts.spotify.com/api/token', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
                'Authorization': 'Basic ' + btoa(clientId + ':' + clientSecret)
            },
            body: 'grant_type=client_credentials'
        });
        const data = await result.json();
        setToken(data.access_token);
        return;
    }

    async function getCurSong(){
        // const result = await fetch('https://api.spotify.com/v1/me/player/currently-playing', {
        //     method: 'GET',
        //     headers: {
        //         'Authorization': 'Bearer ' + token
        //     }
        // });
        // if (result.status == 404){
        //     songName = "No songs is currently playing!"
        //     return null;
        // }
        // const data = await result.json();
        // songName = data.name;
        songName = token;
        document.getElementById("song").value = songName;
    }



    return (
        <div className="bg">
            <Navbar />
            <div className="mvPlayer">
            <iframe
                className="player"
                src={`https://www.youtube.com/embed/${videoId}`}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                title="YouTube video player"
            ></iframe>
            </div>
            <button onClick={getCurSong}>Time to Metro Boomin</button>
            <h1 id="song"></h1>
        </div>

    )
}
