import React, { useState, useEffect } from "react";
import './main.css';
import Navbar from "./components/navbar";

export default function Main(){
    const searchParams = new URLSearchParams(window.location.search);

    //spotify vars
    const [user, setUser] = useState()
    const [isPlaying, setPlaying] = useState(false);
    const [nextState, setNext] = useState(false);

    //spotify authentication
    const [accessToken, setToken] = useState(searchParams.get('access_token'));
    const [refreshToken, setRefresh] = useState(searchParams.get('refresh_token'))

    //spotify song info
    const [song, setSong] = useState("Play a song on your Spotify Player!");
    const [artist, setArtist] = useState();
    const [songImg, setImg] = useState("https://storage.googleapis.com/pr-newsroom-wp/1/2023/05/Spotify_Primary_Logo_RGB_Green.png");

    //spotify artist info
    const [artistImg, setArtistImg] = useState();

    //youtube vars
    const [videoId, setvideoId] = useState("ZncbtRo7RXs?si=ohl4WCuqvY0JJKub");
    const API_KEY = "AIzaSyBxfXvl8QvKRfIQaEJInzvPhKVHCFwPk0o";

    const getRefreshToken = async () => {
        try {
            const result = await fetch(`http://localhost:3001/refresh_token/${refreshToken}`);
            if (!result.ok){
                throw new Error(`Network response was not ok: ${result.statusText}`);
            }
            const token = JSON.parse(await result.text());
            setToken(token.access_token);
            setRefresh(token.refresh_token);
        } catch (error) {
            console.error(error);
        }
    }
    //get new access token every hour
    useEffect(() => {
        const intervalId = setInterval(getRefreshToken, 3600000);
        return () => clearInterval(intervalId);
    })

    const checkPlayState = async () => {
        try {
            const result = await fetch('https://api.spotify.com/v1/me/player', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${accessToken}`
                }
            });
            try {
                const data = JSON.parse(await result.text());
                setPlaying(data.is_playing);
                if (isPlaying){
                    try{
                        if (song != data.item.name){
                            setNext(!nextState);
                        } else {
                            return;
                        }
                        setSong(data.item.name);
                        setArtist(data.item.artists[0]['name']);
                        setImg(data.item.album.images[0].url);
                    } catch {
                        setSong("Advertisement Break")
                        setArtist("Spotify");
                        setImg("https://storage.googleapis.com/pr-newsroom-wp/1/2023/05/Spotify_Primary_Logo_RGB_Green.png");
                    }
                } else {
                    setSong("Play a song on your Spotify Player!");
                    setArtist("Spotify");
                    setImg("https://storage.googleapis.com/pr-newsroom-wp/1/2023/05/Spotify_Primary_Logo_RGB_Green.png")
                }
            } catch {
                setPlaying(false);
            }
        } catch (error){
            console.error(error);
            setPlaying(false);
        }
    }
    useEffect(() => {
        const intervalId = setInterval(checkPlayState, 4000);
        return () => clearInterval(intervalId);
    });

    async function getVideoID(){
        try {
            const result = await fetch(`https://www.googleapis.com/youtube/v3/search?key=${API_KEY}&q=${song} by ${artist}Song Official MV&type=video&part=snippet&maxResults=1`);
            const data = await result.json();
            setvideoId(data.items[0].id.videoId);
        } catch {
            alert("Song not found/limited to 100 songs a day");
            setvideoId("m6pTbEz4w3o?si=63bgCZ2ABohgz8_1");
            return;
        }
    }
    useEffect(() => {
        if (isPlaying){
            getVideoID();
        }
    }, [nextState])

    async function getUserName() {
        try {
            const result = await fetch('https://api.spotify.com/v1/me', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${accessToken}`
                }
            });
            const data = await result.json();
            setUser(data.display_name);
        } catch {
            setUser('ERROR');
        }
    }
    //gets user name everytime the component renders
    useEffect(() => {
        getUserName();
    })

    return (
        <div className="bg">
            <Navbar name={user}/>
            <div className="mvPlayer">
                <iframe
                    className="player"
                    src={`https://www.youtube.com/embed/${videoId}?autoplay=1`}
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    title="YouTube video player"
                ></iframe>
            </div>
            <div className="body">
                <div className="songDescription">
                    <div className="songInfo">
                        <img src={songImg} alt="Album Cover" className="albumCover"></img>
                        <h2 className="songTitle">{song}</h2>
                        <p>{artist}</p>
                    </div>
                </div>

                <div className="faq">
                    <h1>Frequently Asked Questions</h1>
                    <p>Why am I getting an alert message when trying to get a music video?</p>
                    <p>Why am I getting an alert message when trying to get a music video?</p>

                </div>
                <div className="artistDescription">
                    <h1>{artist}</h1>
                </div>
            </div>

        </div>
    )
}
